mapper.select:
select id, boardtitle, boardWriter, boardHits, date_format(createAt, '%Y-%m-%d') as createAt
from board_table
order by id desc;

mapper.update:
update board_table
set boardHits = boardHits + 1
where id = #{id}

update board_table
set boardTitle=#{boardTitle}, boardContents=#{boardContents}
where id=#{id}
